mapper.select:
select nvl(sum(realpayment) -round(((sum(realpayment)*0.058))*0.058),0) sePay, nvl(round((sum(realpayment)*0.058)),0) coPay, count(ordernum) cuPay from ordertbl where orderdate
between #{startDate} and #{endDate}
and ordernum in (select ordernum from orderdetail where not( orderstatus = '환불' or orderstatus = '취소' or orderstatus = '환불 진행중'))

select * from( select * from( select ordernum, od.productnum productnum, productname, mc.mcatenum mcatenum, orderquantity, to_char(orderconfirm, 'YYYY-MM-DD') orderconfirm, orderprice, mc.mcatename mcatename , (orderquantity*orderprice) realpayment from (select ordernum, a.productnum, productname, b.mcatenum, orderquantity, orderconfirm, orderprice, userid, orderstatus from (select ordernum, productnum, orderquantity, orderconfirm, orderprice, orderstatus from orderdetail) a join product b on a.productnum = b.productnum ) od join mcategory mc on od.mcatenum = mc.mcatenum where orderstatus='구매확정' and orderconfirm between add_months(last_day(sysdate)+1,-3) and add_months(last_day(sysdate),0) order by orderconfirm desc, ordernum desc)
where rownum <= ${pageNum*onePageRecord} order by orderconfirm asc, ordernum asc )
where rownum <= ${lastPageRecord}
where rownum <= ${onePageRecord}
order by orderconfirm desc, ordernum desc
