mapper.select:
 		select * from(
	    	select * from(
 				select infonum, infotype, infotitle, to_char(infowritedate, 'YY/MM/DD') infowritedate from notice order by infonum desc
			)
			where rownum<=${pageNum}*${onePageRecord} order by infonum)
			where rownum<=${lastPageRecord}
        							  ${onePageRecord}
        order by infonum
