mapper.select:
      select * from(
	    select * from(
		     select qnum,userid,qcontent,to_char(qwritedate,'MM-DD HH:MI')qwritedate,qopen,qanswer,qtitle from userqboard where userid=#{userid}
		     order by qnum desc)
		     where rownum<=${pageNum}*${onePageRecord} order by qnum asc)
			 where rownum<=${lastPageRecord}
        							  ${onePageRecord}
        	 order by qnum desc
