mapper.select:
select ordernum, to_char(orderdate, 'YYYY-MM-DD') orderdate, orderprice, realpayment, to_char(settledate, 'YYYY-MM-DD') settledate, settlecheck from ( select ordernum, orderdate, orderprice, realpayment, settledate, settlecheck from ( select d.ordernum, d.orderdate, d.orderprice, d.realpayment, c.settledate, c.settlecheck from ( select ordernum, orderstatus, settlecheck, settledate from ( select productnum from product where userid='${userid}' ) a join orderdetail b on a.productnum = b.productnum) c join ordertbl d on c.ordernum = d.ordernum where orderdate BETWEEN '${startDate}' and '${endDate}' and orderstatus='구매확정' order by orderdate asc, ordernum asc) where rownum <= ${pageNum*onePageRecord} order by orderdate desc, ordernum desc)
where rownum <= ${lastPageRecord}
where rownum <= ${onePageRecord}
order by orderdate asc

select ordernum, to_char(orderdate, 'YYYY-MM-DD') orderdate, orderprice, realpayment, to_char(settledate, 'YYYY-MM-DD') settledate, settlecheck from ( select ordernum, orderdate, orderprice, realpayment, settledate, settlecheck from ( select d.ordernum, d.orderdate, d.orderprice, d.realpayment, c.settledate, c.settlecheck from ( select ordernum, orderstatus, settlecheck, settledate from ( select productnum from product where userid='${userid}' ) a join orderdetail b on a.productnum = b.productnum) c join ordertbl d on c.ordernum = d.ordernum where settledate BETWEEN '${startDate}' and '${endDate}' and orderstatus='구매확정' order by settledate asc, orderdate asc) where rownum <= ${pageNum*onePageRecord} order by settledate desc, orderdate desc)
where rownum <= ${lastPageRecord}
where rownum <= ${onePageRecord}
order by settledate asc, orderdate asc

select * from ( select * from ( select to_char(orderdate, 'YYYY-MM-DD') orderdate, sum(d.orderprice) orderprice, sum(d.realpayment) realpayment from ( select ordernum, orderstatus, settlecheck, settledate from ( select productnum from product where userid='${userid}' ) a join orderdetail b on a.productnum = b.productnum where orderstatus='구매확정') c, ordertbl d where c.ordernum = d.ordernum group by orderdate having orderdate BETWEEN '${startDate}' and '${endDate}' order by orderdate asc) where rownum <= ${pageNum*onePageRecord} order by orderdate desc)
where rownum <= ${lastPageRecord}
where rownum <= ${onePageRecord}
order by orderdate asc

select * from ( select * from ( select to_char(settledate, 'YYYY-MM-DD') settledate, sum(d.orderprice) orderprice, sum(d.realpayment) realpayment from ( select ordernum, orderstatus, settlecheck, settledate from ( select productnum from product where userid='${userid}' ) a join orderdetail b on a.productnum = b.productnum where orderstatus='구매확정') c, ordertbl d where c.ordernum = d.ordernum group by settledate having settledate BETWEEN '${startDate}' and '${endDate}' order by settledate desc) where rownum <= ${pageNum*onePageRecord})
where rownum <= ${lastPageRecord}
where rownum <= ${onePageRecord}
order by settledate asc
