mapper.select:
select * from(
select * from(
select * from oto o join (
select distinct roomcode, max(otonum) as b
from oto
where sender=#{userid} or receiver=#{userid}
group by roomcode) r
on o.otonum = r.b
)where rownum<=${pageNum}*${onePageRecord} order by otodate asc
)where rownum<=${lastPageRecord}
						   ${onePageRecord} 
order by otodate desc
