mapper.select:
select * from( select * from( select ordernum, od.productnum productnum, productname, mc.mcatenum mcatenum, orderquantity, to_char(orderconfirm, 'YYYY-MM-DD') orderconfirm, orderprice, mc.mcatename mcatename , (orderquantity*orderprice) realpayment from (select ordernum, a.productnum, productname, b.mcatenum, orderquantity, orderconfirm, orderprice, userid, orderstatus from (select ordernum, productnum, orderquantity, orderconfirm, orderprice, orderstatus from orderdetail) a join product b on a.productnum = b.productnum ) od join mcategory mc on od.mcatenum = mc.mcatenum where userid=#{userid} and orderstatus='구매확정' order by orderconfirm desc, ordernum desc)
where rownum <= ${pageNum*onePageRecord} order by orderconfirm asc, ordernum asc )
where rownum <= ${lastPageRecord}
where rownum <= ${onePageRecord}
order by orderconfirm desc, ordernum desc
