mapper.select:

select ordernum, to_char(orderdate, 'YYYY-MM-DD') orderdate, orderprice, realpayment, to_char(settledate, 'YYYY-MM-DD') settledate, settlecheck 
from ( select d.ordernum ordernum, d.orderdate orderdate, d.orderprice orderprice, d.realpayment realpayment, c.settledate settledate, c.settlecheck settlecheck
from ( select ordernum, orderstatus, settlecheck, settledate 
from ( select productnum from product where userid='${userid}' ) a join orderdetail b on a.productnum = b.productnum) c 
join ordertbl d on c.ordernum = d.ordernum where rownum  <=  ${pageNum*onePageRecord}
and orderdate BETWEEN '${startDate}' and '${endDate}' and orderstatus='구매확정' order by orderdate desc)

	where rownum  <= ${lastPageRecord} 


	where rownum  <= ${onePageRecord} 

order by orderdate asc, ordernum asc
